import React from "react";

const TableRow = ({ item, selectedRowIndex, rowIndex, handleRowClick }) => {
  const handleClick = () => {
    handleRowClick(rowIndex);
  };

  const rowClassName = rowIndex === selectedRowIndex ? "selected" : "";

  return (
    <tr onClick={handleClick} className={rowClassName}>
      <td>{item.id}</td>
      <td>{item.author}</td>
      <td>{item.title}</td>
      <td>{item.kind}</td>
    </tr>
  );
};

export default TableRow;
